mapper.select:
select MENU_ID as menuId , MENU_DIV as menuDiv , MENU_NM as menuNm , MENU_KIND_CD as menuKindCd , MENU_PID as menuPid , MENU_URL as menuUrl , MENU_IMG1 as menuImg1 , MENU_IMG2 as menuImg2 , MENU_ICON as menuIcon , SORT as sort , USE_AUTH as useAuth , USE_AT as useAt , REG_DT as regDt , REG_USER_ID as regUserId from MENU_INFO where 1=1
AND MENU_ID = #{menuId}
AND MENU_DIV = #{menuDiv}
AND MENU_NM = #{menuNm}
AND MENU_KIND_CD = #{menuKindCd}
AND MENU_PID = #{menuPid}
AND USE_AUTH = #{useAuth}
AND USE_AT = #{useAt}
