mapper.select:
 select
	MENU_ID,
	MENU_DIV,
	MENU_NM,
	MENU_KIND_CD,
	MENU_PID,
	MENU_URL,
	MENU_IMG1,
	MENU_IMG2,
	MENU_ICON,
	SORT,
	USE_AUTH,
	USE_AT,
	REG_DT,
	REG_USER_ID
 from MENU_INFO
 where 1=1

	 AND MENU_ID = #{menuId}


	 AND MENU_DIV = #{menuDiv}


	 AND MENU_NM = #{menuNm}


	 AND MENU_KIND_CD = #{menuKindCd}


	 AND MENU_PID = #{menuPid}


	 AND MENU_URL = #{menuUrl}


	 AND MENU_IMG1 = #{menuImg1}


	 AND MENU_IMG2 = #{menuImg2}


	 AND MENU_ICON = #{menuIcon}


	 AND SORT = #{sort}


	 AND USE_AUTH = #{useAuth}


	 AND USE_AT = #{useAt}


	 AND REG_DT = #{regDt}


	 AND REG_USER_ID = #{regUserId}

mapper.update:
 update MENU_INFO
 set
	MENU_ID = #{menuId},
	MENU_DIV = #{menuDiv},
	MENU_NM = #{menuNm},
	MENU_KIND_CD = #{menuKindCd},
	MENU_PID = #{menuPid},
	MENU_URL = #{menuUrl},
	MENU_IMG1 = #{menuImg1},
	MENU_IMG2 = #{menuImg2},
	MENU_ICON = #{menuIcon},
	SORT = #{sort},
	USE_AUTH = #{useAuth},
	USE_AT = #{useAt},
	REG_DT = #{regDt},
	REG_USER_ID = #{regUserId}
 where 
	 MENU_ID = #{menuId}
	 AND MENU_DIV = #{menuDiv}
	 AND MENU_NM = #{menuNm}
	 AND MENU_KIND_CD = #{menuKindCd}
	 AND MENU_PID = #{menuPid}
	 AND MENU_URL = #{menuUrl}
	 AND MENU_IMG1 = #{menuImg1}
	 AND MENU_IMG2 = #{menuImg2}
	 AND MENU_ICON = #{menuIcon}
	 AND SORT = #{sort}
	 AND USE_AUTH = #{useAuth}
	 AND USE_AT = #{useAt}
	 AND REG_DT = #{regDt}
	 AND REG_USER_ID = #{regUserId}

mapper.delete:
 delete from MENU_INFO
 where 
	 MENU_ID = #{menuId}
	 AND MENU_DIV = #{menuDiv}
	 AND MENU_NM = #{menuNm}
	 AND MENU_KIND_CD = #{menuKindCd}
	 AND MENU_PID = #{menuPid}
	 AND MENU_URL = #{menuUrl}
	 AND MENU_IMG1 = #{menuImg1}
	 AND MENU_IMG2 = #{menuImg2}
	 AND MENU_ICON = #{menuIcon}
	 AND SORT = #{sort}
	 AND USE_AUTH = #{useAuth}
	 AND USE_AT = #{useAt}
	 AND REG_DT = #{regDt}
	 AND REG_USER_ID = #{regUserId}
