mapper.select:
select CODE as divCode, CODE_NM as divCodeNm, CODE_DC as divCodeDc, USE_AT as divUseAt from CMMN_CODE where 1=1 AND PARENT_CODE = 'ROOT' AND GRP_CODE != 'CAR'
AND CODE = #{divCode}
AND CODE_NM = #{divCodeNm}
AND USE_AT = #{divUseAt}

select PARENT_CODE as parentCode, CODE as code, CODE_NM as codeNm, CODE_DC as codeDc, USE_AT as useAt, SORT as sort, FRST_REGIST_DT as frstRegistDt, FRST_REGISTER as frstRegister, LAST_REGIST_DT as lastRegistDt, LAST_REGISTER as lastRegister from CMMN_CODE where 1=1 AND GRP_CODE != 'CAR'
AND PARENT_CODE = #{parentCode}
AND CODE = #{code}
AND CODE_NM = #{codeNm}
AND USE_AT = #{useAt}
ORDER BY SORT

mapper.update:
 update CMMN_CODE
 set
	PARENT_CODE = #{parentCode},
	CODE = #{code},
	CODE_NM = #{codeNm},
	CODE_DC = #{codeDc},
	USE_AT = #{useAt},
	SORT = #{sort},
	FRST_REGIST_DT = #{frstRegistDt},
	FRST_REGISTER = #{frstRegister},
	LAST_REGIST_DT = #{lastRegistDt},
	LAST_REGISTER  = #{lastRegister }
 where 
	 PARENT_CODE = #{parentCode}
	 AND CODE = #{code}
	 AND CODE_NM = #{codeNm}
	 AND CODE_DC = #{codeDc}
	 AND USE_AT = #{useAt}
	 AND SORT = #{sort}
	 AND FRST_REGIST_DT = #{frstRegistDt}
	 AND FRST_REGISTER = #{frstRegister}
	 AND LAST_REGIST_DT = #{lastRegistDt}
	 AND LAST_REGISTER  = #{lastRegister }

mapper.delete:
 delete from CMMN_CODE
 where 
	 PARENT_CODE = #{parentCode}

		 AND CODE = #{code}
