mapper.select:
 select
	CAR_COMP as carComp,
	MODEL_CODE as modelCode,
	MODEL_NM as modelNm,
	CAR_KIND as carKind,
	CAR_OUTLINE as carOutline,
	CAR_FURE as carFure,
	CAR_MSN as carMsn
 from CAR_MODEL_INFO
 where 1=1

	 AND CAR_COMP = #{carComp}


	 AND MODEL_CODE = #{modelCode}


	 AND MODEL_NM LIKE CONCAT ('%', #{modelNm} , '%' )


	 AND CAR_KIND = #{carKind}


	 AND CAR_OUTLINE = #{carOutline}


	 AND CAR_FURE = #{carFure}


	 AND CAR_MSN = #{carMsn}

mapper.update:
 update CAR_MODEL_INFO
 set
	MODEL_NM = #{modelNm},
	CAR_KIND = #{carKind},
	CAR_OUTLINE = #{carOutline},
	CAR_FURE = #{carFure},
	CAR_MSN = #{carMsn}
 where 
	 CAR_COMP = #{carComp}
	 AND MODEL_CODE = #{modelCode}

mapper.delete:
 delete from CAR_MODEL_INFO
 where 
	 CAR_COMP = #{carComp}
	 AND MODEL_CODE = #{modelCode}

mapper.select:
SELECT CODE as code, CODE_NM as codeNm FROM CMMN_CODE WHERE 1=1 AND GRP_CODE = 'CAR' AND USE_AT = 'Y'
AND PARENT_CODE IN ( SELECT CODE FROM CMMN_CODE WHERE GRP_CODE = 'CAR' AND PARENT_CODE = 'CAR_COMP' AND USE_AT = 'Y' )
AND PARENT_CODE = #{parentCode}
AND CODE = #{code}
AND CODE_NM LIKE CONCAT('%', #{codeNm} , '%' )
ORDER BY CODE
