mapper.select:
 select
	PRKPLCE_CODE,
	PRKPLCE_FLR_CODE,
	CELL_MAPNG_ID,
	CELL_TYPE,
	STYLE_CLS,
	SALE_CAR_INNB,
	X,
	Y,
	WIDTH,
	HEIGHT
 from PRKPLCE_CELL_MNG
 where 1=1

	 AND PRKPLCE_CODE = #{prkplceCode}


	 AND PRKPLCE_FLR_CODE = #{prkplceFlrCode}


	 AND CELL_MAPNG_ID = #{cellMapngId}


	 AND CELL_TYPE = #{cellType}


	 AND STYLE_CLS = #{styleCls}


	 AND SALE_CAR_INNB = #{saleCarInnb}


	 AND X = #{x}


	 AND Y = #{y}


	 AND WIDTH = #{width}


	 AND HEIGHT = #{height}

mapper.update:
 update PRKPLCE_CELL_MNG
 set
	PRKPLCE_CODE = #{prkplceCode},
	PRKPLCE_FLR_CODE = #{prkplceFlrCode},
	CELL_MAPNG_ID = #{cellMapngId},
	CELL_TYPE = #{cellType},
	STYLE_CLS = #{styleCls},
	SALE_CAR_INNB = #{saleCarInnb},
	X = #{x},
	Y = #{y},
	WIDTH = #{width},
	HEIGHT = #{height}
 where 
	 PRKPLCE_CODE = #{prkplceCode}
	 AND PRKPLCE_FLR_CODE = #{prkplceFlrCode}
	 AND CELL_MAPNG_ID = #{cellMapngId}
	 AND CELL_TYPE = #{cellType}
	 AND STYLE_CLS = #{styleCls}
	 AND SALE_CAR_INNB = #{saleCarInnb}
	 AND X = #{x}
	 AND Y = #{y}
	 AND WIDTH = #{width}
	 AND HEIGHT = #{height}

mapper.delete:
 delete from PRKPLCE_CELL_MNG
 where 
	 PRKPLCE_CODE = #{prkplceCode}
	 AND PRKPLCE_FLR_CODE = #{prkplceFlrCode}
	 AND CELL_MAPNG_ID = #{cellMapngId}
	 AND CELL_TYPE = #{cellType}
	 AND STYLE_CLS = #{styleCls}
	 AND SALE_CAR_INNB = #{saleCarInnb}
	 AND X = #{x}
	 AND Y = #{y}
	 AND WIDTH = #{width}
	 AND HEIGHT = #{height}
