mapper.select:
 select
	PRKPLCE_CODE,
	PRKPLCE_FLR_CODE,
	CELL_MAPNG_ID,
	CELL_TYPE,
	STYLE_CLS,
	SALE_CAR_INNB,
	X,
	Y,
	TRANSFORM,
	SHAPE,
	WIDTH,
	HEIGHT
 from PRKPLCE_CELL_MNG
 where 1=1

	 AND PRKPLCE_CODE = #{prkplceCode}


	 AND PRKPLCE_FLR_CODE = #{prkplceFlrCode}


	 AND CELL_MAPNG_ID = #{cellMapngId}


	 AND CELL_TYPE = #{cellType}


	 AND STYLE_CLS = #{styleCls}


	 AND SALE_CAR_INNB = #{saleCarInnb}


	 AND X = #{x}


	 AND Y = #{y}


	 AND WIDTH = #{width}


	 AND HEIGHT = #{height}


	 AND TRANSFORM = #{transform}


	 AND SHAPE = #{shape}

mapper.delete:
 delete from PRKPLCE_CELL_MNG
 where
 1=1 

		 AND PRKPLCE_CODE = #{prkplceCode}


		 AND PRKPLCE_FLR_CODE = #{prkplceFlrCode}


		 AND CELL_MAPNG_ID = #{cellMapngId}

mapper.select:
 select
	PRKPLCE_CODE,
	PRKPLCE_FLR_CODE,
	PRKPLCE_FLR_NM,
	DRW_PATH,
	DRW_SIZE_WIDTH,
	DRW_SIZE_HEIGHT,
	SORT,
	RM
 from PRKPLCE_FLR_MNG
 where 1=1

	 AND PRKPLCE_CODE = #{prkplceCode}


	 AND PRKPLCE_FLR_CODE = #{prkplceFlrCode}


	 AND PRKPLCE_FLR_NM = #{prkplceFlrNm}


	 AND DRW_PATH = #{drwPath}


	 AND DRW_SIZE_WIDTH = #{drwSizeWidth}


	 AND DRW_SIZE_HEIGHT = #{drwSizeHeight}


	 AND SORT = #{sort}


	 AND RM = #{rm}

mapper.update:
 update PRKPLCE_FLR_MNG
 set
 	RM = ''

		 ,DRW_PATH = #{drwPath}


		 ,DRW_SIZE_WIDTH = #{drwSizeWidth}


		 ,DRW_SIZE_HEIGHT = #{drwSizeHeight}


		 ,SORT = #{sort}


		 ,RM = #{rm}

 where 
	 PRKPLCE_CODE = #{prkplceCode}
	 AND PRKPLCE_FLR_CODE = #{prkplceFlrCode}

mapper.delete:
 delete from PRKPLCE_FLR_MNG
 where 
	 PRKPLCE_CODE = #{prkplceCode}
	 AND PRKPLCE_FLR_CODE = #{prkplceFlrCode}
